import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ViewRecipes = ({ user }) => {
  const [apiKey, setApiKey] = useState(user.api_key || '');
  const [query, setQuery] = useState('');
  const [recipes, setRecipes] = useState([]);

  const searchRecipes = async () => {
    try {
      const res = await axios.get(`http://localhost:3000/api/recipes/search?api_key=${apiKey}&q=${query}`);
      setRecipes(res.data);
    } catch (err) {
      alert(err.response?.data?.message || "Gagal mencari resep");
    }
  };

  const inputStyle = { width: '100%', padding: '12px', margin: '10px 0', borderRadius: '8px', border: '1px solid #E8D1C5', boxSizing: 'border-box' };

  return (
    <div style={{ padding: '40px', maxWidth: '800px', margin: 'auto' }}>
      <Link to="/dashboard" style={{ color: '#452829', textDecoration: 'none' }}>← Kembali ke Dashboard</Link>
      <h1 style={{ marginTop: '20px', color: '#452829' }}>Recipe Explorer</h1>
      
      <div style={{ background: 'white', padding: '25px', borderRadius: '15px', border: '1px solid #E8D1C5', marginBottom: '30px' }}>
        <label>API Key Anda:</label>
        <input type="text" value={apiKey} onChange={(e) => setApiKey(e.target.value)} style={inputStyle} />
        <label>Cari Bahan (Contoh: Ikan, Ayam):</label>
        <input type="text" placeholder="Masukkan bahan..." value={query} onChange={(e) => setQuery(e.target.value)} style={inputStyle} />
        <button onClick={searchRecipes} style={{ width: '100%', padding: '12px', background: '#452829', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' }}>
          CARI RESEP
        </button>
      </div>

      <div style={{ display: 'grid', gap: '20px' }}>
        {recipes.length > 0 ? (
          recipes.map((r) => (
            <div key={r.id} style={{ background: 'white', padding: '20px', borderRadius: '15px', borderLeft: '6px solid #452829', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
              <h3 style={{ margin: '0 0 10px 0', color: '#452829' }}>{r.title}</h3>
              <p style={{ color: '#666', fontSize: '14px' }}>{r.instructions}</p>
              <div style={{ marginTop: '10px', fontWeight: 'bold' }}>⭐ Rating: {r.avg_rating}</div>
            </div>
          ))
        ) : (
          <p style={{ textAlign: 'center', color: '#999' }}>Data tidak ditemukan. Pastikan API Key benar dan bahan ada di database.</p>
        )}
      </div>
    </div>
  );
};

export default ViewRecipes;
