import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ViewRecipes = ({ user }) => {
  const [apiKey, setApiKey] = useState(user.api_key || '');
  const [query, setQuery] = useState('');
  const [recipes, setRecipes] = useState([]);

  const searchRecipes = async () => {
    try {
      const res = await axios.get(`http://localhost:3000/api/recipes/search?api_key=${apiKey}&q=${query}`);
      setRecipes(res.data);
    } catch (err) {
      alert(err.response?.data?.message || "Gagal mengambil data");
    }
  };

  return (
    <div style={{ padding: '40px', maxWidth: '800px', margin: 'auto' }}>
      <Link to="/dashboard" style={{ color: '#452829' }}>← Kembali ke Dashboard</Link>
      <h1 style={{ marginTop: '20px' }}>Recipe Explorer</h1>
      
      <div style={{ background: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #E8D1C5', marginBottom: '20px' }}>
        <input type="text" placeholder="Masukkan API Key Anda" value={apiKey} onChange={(e) => setApiKey(e.target.value)} style={inputStyle} />
        <input type="text" placeholder="Cari Bahan (cth: Ayam)" value={query} onChange={(e) => setQuery(e.target.value)} style={inputStyle} />
        <button onClick={searchRecipes} style={btnStyle}>CARI RESEP</button>
      </div>

      <div>
        {recipes.map((r) => (
          <div key={r.id} style={recipeCard}>
            <h3>{r.title}</h3>
            <p>{r.instructions}</p>
            <small>Rating: ⭐ {r.avg_rating}</small>
          </div>
        ))}
      </div>
    </div>
  );
};

const inputStyle = { width: '100%', padding: '12px', margin: '5px 0', borderRadius: '8px', border: '1px solid #E8D1C5', boxSizing: 'border-box' };
const btnStyle = { width: '100%', padding: '12px', background: '#452829', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold', marginTop: '10px' };
const recipeCard = { background: 'white', padding: '20px', borderRadius: '12px', borderLeft: '6px solid #452829', marginBottom: '15px' };

export default ViewRecipes;
