import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import WeatherCard from './WeatherCard';

const Dashboard = ({ user, setUser }) => {
  const [allUsers, setAllUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Hanya Admin yang mengambil data seluruh user
    if (user.role === 'ADMIN') {
      axios.get('http://localhost:3000/api/admin/users')
        .then(res => setAllUsers(res.data))
        .catch(err => console.error("Gagal ambil data admin:", err));
    }
  }, [user.role]);

  const generateKey = async () => {
    try {
      const res = await axios.post('http://localhost:3000/api/generate-key', { userId: user.id });
      const updatedUser = { ...user, api_key: res.data.api_key };
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      alert("API Key Berhasil Dibuat!");
    } catch (err) { alert("Gagal generate key"); }
  };

  const logout = () => {
    localStorage.clear();
    setUser(null);
    navigate('/login');
  };

  const tdStyle = { border: '1px solid #E8D1C5', padding: '12px', textAlign: 'left' };

  return (
    <div style={{ padding: '40px', maxWidth: '900px', margin: 'auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1>Savory Dashboard</h1>
        <button onClick={logout} style={{ color: '#452829', cursor: 'pointer', background: 'none', border: 'none', textDecoration: 'underline' }}>Logout</button>
      </div>

      <p>Halo, <strong>{user.username}</strong>! Anda masuk sebagai <strong>{user.role}</strong>.</p>

      {user.role === 'ADMIN' ? (
        <div style={{ background: 'white', padding: '25px', borderRadius: '15px', border: '1px solid #E8D1C5' }}>
          <h3>Monitoring List User</h3>
          <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
            <thead>
              <tr style={{ background: '#452829', color: 'white' }}>
                <th style={tdStyle}>ID</th>
                <th style={tdStyle}>Username</th>
                <th style={tdStyle}>Email</th>
                <th style={tdStyle}>API Key</th>
              </tr>
            </thead>
            <tbody>
              {allUsers.map(u => (
                <tr key={u.id}>
                  <td style={tdStyle}>{u.id}</td>
                  <td style={tdStyle}>{u.username}</td>
                  <td style={tdStyle}>{u.email}</td>
                  <td style={tdStyle}><code>{u.api_key || '-'}</code></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div style={{ background: 'white', padding: '25px', borderRadius: '15px', border: '1px solid #E8D1C5', textAlign: 'center' }}>
          <h3>Akses API Anda</h3>
          <div style={{ background: '#F3E8DF', padding: '15px', margin: '15px 0', fontFamily: 'monospace', borderRadius: '8px' }}>
            {user.api_key || 'Klik tombol di bawah untuk buat Key'}
          </div>
          <button onClick={generateKey} style={{ background: '#452829', color: 'white', padding: '12px 25px', border: 'none', borderRadius: '8px', cursor: 'pointer' }}>
            GENERATE NEW KEY
          </button>
          <br /><br />
          <button onClick={() => navigate('/recipes')} style={{ background: '#745747', color: '#452829', padding: '10px 20px', border: 'none', borderRadius: '8px', cursor: 'pointer' }}>
            BUKA CARI RESEP â†’
          </button>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
