import React, { useState } from 'react';
import axios from 'axios';

const WeatherCard = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);

  // API Key milikmu
  const API_KEY = 'f6804d7d103d4aa78a940503250610'; 

  const getWeather = async () => {
    if (!city) return;
    setLoading(true);
    try {
      // Endpoint khusus WeatherAPI.com
      const res = await axios.get(
        `http://api.weatherapi.com/v1/current.json?key=${API_KEY}&q=${city}&aqi=no`
      );
      setWeather(res.data);
    } catch (err) {
      alert("Kota tidak ditemukan atau API Key bermasalah.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={cardStyle}>
      <h3 style={{ marginTop: 0 }}>Cek Cuaca Sebelum Masak üå§Ô∏è</h3>
      <div style={{ display: 'flex', gap: '10px' }}>
        <input 
          type="text" 
          placeholder="Ketik kota (contoh: Jakarta)..." 
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={inputStyle}
        />
        <button onClick={getWeather} style={btnStyle}>
          {loading ? '...' : 'Cek'}
        </button>
      </div>

      {weather && (
        <div style={{ marginTop: '20px', display: 'flex', alignItems: 'center', gap: '15px' }}>
          <img src={weather.current.condition.icon} alt="icon" />
          <div>
            <h2 style={{ margin: 0 }}>{weather.current.temp_c}¬∞C</h2>
            <p style={{ margin: 0 }}>{weather.location.name}, {weather.current.condition.text}</p>
          </div>
        </div>
      )}
    </div>
  );
};

// Styling
const cardStyle = { background: 'white', padding: '20px', borderRadius: '15px', border: '1px solid #E8D1C5', marginTop: '20px' };
const inputStyle = { flex: 1, padding: '10px', borderRadius: '8px', border: '1px solid #E8D1C5' };
const btnStyle = { padding: '10px 20px', background: '#452829', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer' };

export default WeatherCard;
