import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ViewRecipes = ({ user }) => {
  const [query, setQuery] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);

  // API Key Spoonacular milikmu
  const SPOONACULAR_KEY = 'a20dc61815ad4bb892ebe57f2c7278c4';

  const searchRecipes = async () => {
    if (!query) return;
    setLoading(true);
    try {
      // Memanggil API Spoonacular secara langsung
      const res = await axios.get(
        `https://api.spoonacular.com/recipes/complexSearch?apiKey=${SPOONACULAR_KEY}&query=${query}&number=10`
      );
      setRecipes(res.data.results);
    } catch (err) {
      alert("Gagal mengambil data. Pastikan API Key aktif dan internet lancar.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '40px', maxWidth: '800px', margin: 'auto' }}>
      <Link to="/dashboard" style={{ color: '#452829', textDecoration: 'none', fontWeight: 'bold' }}>
        ‚Üê Kembali ke Dashboard
      </Link>
      
      <h1 style={{ color: '#452829', marginTop: '20px' }}>Global Recipe Explorer</h1>
      
      <div style={{ background: 'white', padding: '25px', borderRadius: '15px', border: '1px solid #E8D1C5', marginBottom: '30px' }}>
        <input 
          type="text" 
          placeholder="Cari dalam Bahasa Inggris (contoh: Chicken, Pasta, Pizza)..." 
          value={query} 
          onChange={(e) => setQuery(e.target.value)} 
          style={{ width: '100%', padding: '12px', borderRadius: '8px', border: '1px solid #E8D1C5', boxSizing: 'border-box', marginBottom: '10px' }}
        />
        <button 
          onClick={searchRecipes} 
          style={{ width: '100%', padding: '12px', background: '#452829', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' }}
        >
          {loading ? 'MENCARI...' : 'CARI RESEP DI SPOONACULAR'}
        </button>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '20px' }}>
        {recipes.length > 0 ? (
          recipes.map((r) => (
            <div key={r.id} style={{ background: 'white', borderRadius: '15px', border: '1px solid #E8D1C5', overflow: 'hidden', textAlign: 'center' }}>
              <img src={r.image} alt={r.title} style={{ width: '100%', height: '150px', objectFit: 'cover' }} />
              <div style={{ padding: '10px' }}>
                <h4 style={{ color: '#452829', fontSize: '14px', margin: '10px 0' }}>{r.title}</h4>
              </div>
            </div>
          ))
        ) : (
          !loading && <p style={{ gridColumn: '1/-1', textAlign: 'center', color: '#999' }}>Data tidak ditemukan. Coba cari "Pasta" atau "Chicken".</p>
        )}
      </div>
    </div>
  );
};

export default ViewRecipes;
