import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import ViewRecipes from './pages/ViewRecipes';

function App() {
  // Mengambil data user dari localStorage jika sudah login sebelumnya
  const [user, setUser] = useState(() => {
    const savedUser = localStorage.getItem('user');
    return savedUser ? JSON.parse(savedUser) : null;
  });

  return (
    <Router>
      <div style={{ backgroundColor: '#F3E8DF', minHeight: '100vh' }}>
        <Routes>
          {/* Halaman Login */}
          <Route path="/login" element={!user ? <Login setUser={setUser} /> : <Navigate to="/dashboard" />} />

          {/* Halaman Register */}
          <Route path="/register" element={!user ? <Register /> : <Navigate to="/dashboard" />} />

          {/* Halaman Dashboard (Hanya bisa dibuka jika sudah Login) */}
          <Route path="/dashboard" element={user ? <Dashboard user={user} setUser={setUser} /> : <Navigate to="/login" />} />

          {/* Halaman Fitur Utama (Cari Resep) */}
          <Route path="/recipes" element={user ? <ViewRecipes user={user} /> : <Navigate to="/login" />} />

          {/* Jika link ngawur, arahkan ke login */}
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
