import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import WeatherCard from './WeatherCard';

const Dashboard = ({ user, setUser }) => {
  const [allUsers, setAllUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Hanya Admin yang mengambil data seluruh user dari backend
    if (user.role === 'ADMIN') {
      axios.get('http://localhost:3000/api/admin/users')
        .then(res => setAllUsers(res.data))
        .catch(err => console.error("Gagal ambil data admin:", err));
    }
  }, [user.role]);

  const generateKey = async () => {
    try {
      const res = await axios.post('http://localhost:3000/api/generate-key', { userId: user.id });
      const updatedUser = { ...user, api_key: res.data.api_key };
      
      // Update data di localStorage dan State agar tampilan langsung berubah
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      alert("API Key Berhasil Dibuat!");
    } catch (err) { 
      alert("Gagal generate key"); 
    }
  };

  const logout = () => {
    localStorage.clear();
    setUser(null);
    navigate('/login');
  };

  const tdStyle = { border: '1px solid #E8D1C5', padding: '12px', textAlign: 'left' };

  return (
    <div style={{ padding: '40px', maxWidth: '900px', margin: 'auto' }}>
      {/* Header Dashboard */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1 style={{ color: '#452829' }}>Savory Dashboard</h1>
        <button onClick={logout} style={{ color: '#452829', cursor: 'pointer', background: 'none', border: 'none', textDecoration: 'underline' }}>
          Logout
        </button>
      </div>

      <p style={{ marginBottom: '20px' }}>
        Halo, <strong>{user.username}</strong>! Anda masuk sebagai <strong>{user.role}</strong>.
      </p>

      {/* FITUR CUACA (Muncul untuk Admin maupun User) */}
      <WeatherCard />

      <div style={{ marginTop: '30px' }}>
        {user.role === 'ADMIN' ? (
          /* TAMPILAN KHUSUS ADMIN */
          <div style={{ background: 'white', padding: '25px', borderRadius: '15px', border: '1px solid #E8D1C5', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
            <h3 style={{ color: '#452829', marginTop: 0 }}>Monitoring List User</h3>
            <div style={{ overflowX: 'auto' }}>
              <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
                <thead>
                  <tr style={{ background: '#452829', color: 'white' }}>
                    <th style={tdStyle}>ID</th>
                    <th style={tdStyle}>Username</th>
                    <th style={tdStyle}>Email</th>
                    <th style={tdStyle}>API Key</th>
                  </tr>
                </thead>
                <tbody>
                  {allUsers.map(u => (
                    <tr key={u.id}>
                      <td style={tdStyle}>{u.id}</td>
                      <td style={tdStyle}>{u.username}</td>
                      <td style={tdStyle}>{u.email}</td>
                      <td style={tdStyle}><code style={{ background: '#F3E8DF', padding: '2px 5px', borderRadius: '4px' }}>{u.api_key || '-'}</code></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ) : (
          /* TAMPILAN KHUSUS USER BIASA */
          <div style={{ background: 'white', padding: '25px', borderRadius: '15px', border: '1px solid #E8D1C5', textAlign: 'center', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
            <h3 style={{ color: '#452829', marginTop: 0 }}>Akses API Anda</h3>
            <p style={{ color: '#666' }}>Gunakan Key di bawah ini untuk mengakses fitur pencarian resep.</p>
            <div style={{ background: '#F3E8DF', padding: '15px', margin: '15px 0', fontFamily: 'monospace', borderRadius: '8px', fontSize: '18px', fontWeight: 'bold', color: '#452829', border: '1px dashed #452829' }}>
              {user.api_key || 'Klik tombol di bawah untuk buat Key'}
            </div>
            <button onClick={generateKey} style={{ background: '#452829', color: 'white', padding: '12px 25px', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold', width: '100%', marginBottom: '10px' }}>
              GENERATE NEW KEY
            </button>
            <button onClick={() => navigate('/recipes')} style={{ background: '#E8D1C5', color: '#452829', padding: '12px 25px', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold', width: '100%' }}>
              BUKA CARI RESEP â†’
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
